import logo from './logo.svg';
import './App.css';
import Footer from './Mycomponents/Footer';
import Todos from './Mycomponents/Todos';
import Header from './Mycomponents/Header';
import AddTodo from './Mycomponents/AddTodo';
import React, {useState} from 'react';

function App() {

  const onDelete=(todo)=>{
    console.log("Delete button is clicked",todo);
    //let index = todos.indexOf(todo);
    //todos.splice(index,1);
    setTodos(todos.filter((e)=>{
      return e!==todo;
    }));
  }
  const addTodo=(title,desc)=>{
    console.log("Add todo button is clicked",title,desc);
    let sno=todos[todos.lenght-1].sno+1;
    const myTodo={
      sno:sno,
      title:title,
      desc:desc,
    }
    console.log(myTodo);
  }
  const [todos, setTodos] =useState ([
    {
    sno: 1,
    title: 'Buy Milk',
    desc: 'You need to go the market'
  },{
    sno: 2,
    title: 'Buy Cotton',
    desc: 'You need to go the cloth store'
  },{
    sno: 3,
    title: 'Buy meds',
    desc: 'You need to go the medical shop'
  }
  ]);
  return (
    <>
      <Header title='My Todos List'/>
      <AddTodo addTodo={addTodo}/>
      <Todos todos={todos} onDelete={onDelete}/>
      <Footer/>
    </>
  );
}

export default App;
